// App.js
import React, { useState } from 'react';
import './App.css';
import TextInput from './TextInput';

function formatResult(result) {
  const formattedResult = [
    `Word count: ${result['Word count']},`,
    `Average word length: ${result['Average word length']},`,
    'Number of words of length:',
  ];

  for (const [key, value] of Object.entries(result['Number of words of length'])) {
    formattedResult.push(`  - "${key}": ${value}`);
  }

  formattedResult.push(
    `The most frequently occurring word length is ${result['The most frequently occurring word length is 1 for word lengths of']} for word lengths of: ${result['The most frequently occurring word length is 1 for word lengths of']}`
  );

  return formattedResult.join('\n');
}

function App() {
  const [result, setResult] = useState(null);

  const handleTextSubmit = async (text) => {
    try {
      const response = await fetch('http://localhost:5000/count-words', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ file: text }),
      });

      if (response.ok) {
        const resultData = await response.json();
        const formattedResult = formatResult(resultData);
        setResult(formattedResult);
      } else {
        console.error('Server returned an error');
      }
    } catch (error) {
      console.error('Error occurred during fetch:', error);
    }
  };

  const formatResult = (resultData) => {
    return (
      <div>
        <p>Word count: {resultData['Word count']}</p>
        <p>Average word length: {resultData['Average word length']}</p>
        <p>Number of words of length:</p>
        <ul>
          {Object.entries(resultData['Number of words of length']).map(([key, value]) => (
            <li key={key}>{`${key}: ${value}`}</li>
          ))}
        </ul>
        <p>
          The most frequently occurring word length is ({resultData['The most frequently occurring word length is']}{' '}
          for word lengths of: ({resultData['The most frequently occurring word length is']})
        </p>
      </div>
    );
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Gimme some words. I will count them and stuff.</h1>
        <TextInput onSubmit={handleTextSubmit} />

        {result && (
          <div>
            <h2>Analysis Result:</h2>
            {result}
          </div>
        )}
      </header>
    </div>
  );
}

export default App;
