// src/TextInput.js
import React, { useState } from 'react';
import axios from 'axios';

const TextInput = ({ onSubmit }) => {
  const [text, setText] = useState('');
  const [result, setResult] = useState(null);

  const handleCountWords = async () => {
    try {
      const response = await axios.post('http://localhost:5000/count-words', { file: text });
      setResult(response.data);
      onSubmit(response.data); // Pass the result to the parent component
    } catch (error) {
      console.error('Error counting words:', error);
    }
  };

  return (
    <div>
      <textarea
        value={text}
        onChange={(e) => setText(e.target.value)}
        placeholder="Type or paste your text here..."
      />
      <br />
      <button onClick={handleCountWords}>Count Words</button>

      {result && (
        <div>
          <h2>Word Count Results:</h2>
          {/* Remove JSON.stringify and display the formatted result */}
          <pre>{result}</pre>
        </div>
      )}
    </div>
  );
};

export default TextInput;
